  }

  .dark .sidebar-item:hover {
    background: rgba(33, 39, 55, 0.6);
  }

  .dark .sidebar-item.active {
    background: rgba(33, 39, 55, 0.8);
    border-left: 2px solid rgba(0, 255, 204, 1);
    box-shadow: 0 2px 8px 0 rgba(0, 255, 204, 0.15);
  }

  /* Custom scrollbar for dark theme */
  .dark .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .dark .custom-scrollbar::-webkit-scrollbar-track {
    background: hsl(210 15% 12%);
  }

  .dark .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(210 15% 25%);
    border-radius: 3px;
  }

  .dark .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(210 15% 35%);
  }

  /* Enhanced gradient text for dark theme */
  .dark .gradient-text {
    background: linear-gradient(135deg, hsl(175.36 100% 55%) 0%, hsl(159.7826 100% 45%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Enhanced button hover effects for dark theme */
  .dark .hover-lift:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px 0 rgba(0, 255, 204, 0.15);
  }

  /* Enhanced animations and effects */
  .glass-card {
    @apply bg-card border border-border;
  }

  .btn-primary-modern {
    background: hsl(var(--primary));
    color: hsl(var(--primary-foreground));
  }

  .btn-primary-modern:hover {
    background: hsl(var(--primary));
    opacity: 0.9;
  }

  .card-modern {
    @apply bg-card border border-border rounded-lg;
  }

  .sidebar-item {
    @apply p-3 rounded-lg hover:bg-accent transition-all duration-200;
  }

  .sidebar-item.active {
    @apply bg-accent border-l-2 border-primary;
  }

  /* Custom scrollbar */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: hsl(var(--muted));
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--muted-foreground));
    border-radius: 3px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--accent-foreground));
  }

  /* Gradient text */
  .gradient-text {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .animate-pulse-subtle {
    animation: pulseSubtle 2s infinite;
  }

  .animate-gradient {
    animation: gradient 3s ease infinite;
  }

  .animate-bounce-gentle {
    animation: bounceGentle 2s infinite;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-scale-in {
    animation: scaleIn 0.5s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.6s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-slide-in-right {
    animation: slideInRight 0.6s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-fade-in-up {
    animation: fadeInUp 0.6s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-fade-in-down {
    animation: fadeInDown 0.6s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-rotate-in {
    animation: rotateIn 0.7s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-zoom-in {
    animation: zoomIn 0.4s cubic-bezier(0.16, 1, 0.3, 1);
  }

  .animate-elastic {
    animation: elastic 0.8s cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }

  .animate-wobble {
    animation: wobble 1s ease-in-out;
  }

  .animate-flip {
    animation: flip 0.6s ease-in-out;
  }

  .animate-heart-beat {
    animation: heartBeat 1.5s infinite;
  }

  .animate-rubber-band {
    animation: rubberBand 1s ease-in-out;
  }

  /* Advanced animations */
  .animate-shimmer {
    background: linear-gradient(110deg, transparent 40%, rgba(255,255,255,0.1) 50%, transparent 60%);
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  .animate-floating {
    animation: floating 3s ease-in-out infinite;
  }

  .animate-gradient-shift {
    animation: gradientShift 3s ease infinite;
    background-size: 200% 200%;
  }

  .animate-pulse-subtle {
    animation: pulseSubtle 4s ease-in-out infinite;
  }

  /* Glass morphism utilities */
  .glass-card {
    @apply backdrop-blur-xl bg-white/80 dark:bg-gray-900/80 border border-white/20 dark:border-gray-700/50 shadow-xl;
  }

  .glass-sidebar {
    @apply backdrop-blur-2xl bg-sidebar border-r border-sidebar-border shadow-2xl;
    background-color: hsl(var(--sidebar) / 0.95);
    border-color: hsl(var(--sidebar-border) / 0.5);
  }

  /* Professional hover effects */
  .hover-lift {
    @apply transition-all duration-300 ease-out;
    transform: translateY(0);
  }

  .hover-lift:hover {
    transform: translateY(-2px);
    box-shadow: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 10px 10px -5px rgb(0 0 0 / 0.04);
  }

  .hover-scale {
    @apply transition-transform duration-300 ease-out;
  }

  .hover-scale:hover {
    transform: scale(1.05);
  }

  .hover-rotate {
    @apply transition-transform duration-300 ease-out;
  }

  .hover-rotate:hover {
    transform: rotate(5deg);
  }

  .hover-glow {
    @apply transition-all duration-300 ease-out;
  }

  .hover-glow:hover {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.5);
  }

  .hover-bounce {
    @apply transition-transform duration-200 ease-out;
  }

  .hover-bounce:hover {
    animation: bounceGentle 0.6s ease-in-out;
  }

  /* Professional gradients */
  .gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
  }

  .gradient-text {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Modern scrollbars */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    @apply bg-muted rounded-full opacity-30;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    @apply bg-muted-foreground rounded-full opacity-20 hover:opacity-30;
  }

  /* Professional focus styles */
  .focus-ring {
    @apply focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 focus:ring-offset-background;
  }

  /* Message styles */
  .message-user {
    @apply bg-primary text-primary-foreground rounded-2xl rounded-tr-md px-4 py-3 max-w-[80%] ml-auto;
  }

  .message-assistant {
    @apply bg-muted text-muted-foreground rounded-2xl rounded-tl-md px-4 py-3 max-w-[85%];
  }

  .message-loading {
    @apply bg-muted text-muted-foreground rounded-2xl rounded-tl-md px-4 py-3 max-w-[85%] animate-pulse;
  }

  /* Professional buttons */
  .btn-modern {
    @apply inline-flex items-center justify-center gap-2 rounded-xl px-6 py-3 text-sm font-semibold
           transition-all duration-300 ease-out focus-ring hover-lift
           disabled:pointer-events-none disabled:opacity-50
           relative overflow-hidden;
  }

  .btn-modern::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s;
  }

  .btn-modern:hover::before {
    left: 100%;
  }

  .btn-primary-modern {
    @apply btn-modern bg-primary text-primary-foreground shadow-lg;
  }

  .btn-primary-modern:hover {
    background-color: hsl(var(--primary) / 0.9);
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -5px hsl(var(--primary) / 0.3);
  }

  .btn-primary-modern:active {
    transform: translateY(-1px);
  }

  .btn-secondary-modern {
    @apply btn-modern bg-secondary text-secondary-foreground border;
  }

  .btn-secondary-modern:hover {
    background-color: hsl(var(--secondary) / 0.8);
  }

  .btn-ghost-modern {
    @apply btn-modern;
  }

  .btn-ghost-modern:hover {
    @apply bg-accent text-accent-foreground;
  }

  /* Enhanced form inputs */
  .input-modern {
    @apply w-full rounded-xl border-2 border-border bg-background px-4 py-3.5
           text-sm transition-all duration-200 ease-out placeholder:text-muted-foreground
           focus:border-primary focus:outline-none focus:ring-0
           disabled:cursor-not-allowed disabled:opacity-50;
  }

  .input-modern:focus {
    background-color: hsl(var(--background) / 0.5);
  }

  .input-modern:hover {
    border-color: hsl(var(--border));
  }

  /* Professional cards */
  .card-modern {
    @apply rounded-2xl border bg-card p-6 shadow-sm transition-all duration-200 ease-out
           hover:shadow-lg hover-lift backdrop-blur-sm;
  }

  /* Sidebar item styles */
  .sidebar-item {
    @apply relative flex items-center gap-3 rounded-xl px-3 py-2.5 text-sm font-medium
           transition-all duration-200 ease-out cursor-pointer
           hover:bg-sidebar-accent hover:text-sidebar-accent-foreground
           focus-ring;
  }

  .sidebar-item.active {
    @apply bg-sidebar-primary text-sidebar-primary-foreground shadow-sm;
  }

  .sidebar-item.active::before {
    @apply absolute left-0 top-1/2 h-6 w-1 -translate-y-1/2 bg-sidebar-primary-foreground rounded-r-full opacity-80;
    content: '';
  }

  /* Loading states */
  .skeleton {
    @apply bg-muted animate-pulse rounded;
  }

  .loading-dots::after {
    content: '';
    animation: loadingDots 1.4s infinite;
  }

  /* Responsive utilities */
  .responsive-padding {
    @apply px-4 sm:px-6 lg:px-8;
  }

  .responsive-text {
    @apply text-sm sm:text-base lg:text-lg;
  }
}

/* Enhanced keyframes */
@keyframes fadeIn {
  0% {
    opacity: 0;
    transform: translateY(16px) scale(0.96);
  }
  100% {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

@keyframes slideUp {
  0% {
    transform: translateY(100%) scale(0.95);
    opacity: 0;
  }
  100% {
    transform: translateY(0) scale(1);
    opacity: 1;
  }
}

@keyframes typing {
  0%, 100% {
    opacity: 0.3;
  }
  50% {
    opacity: 1;
  }
}

@keyframes pulseSubtle {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.8;
  }
}

@keyframes gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

@keyframes bounceGentle {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-8px);
  }
}

@keyframes loadingDots {
  0%, 20% {
    content: '.';
  }
  40% {
    content: '..';
  }
  60%, 100% {
    content: '...';
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes glow {
  from {
    box-shadow: 0 0 10px hsl(var(--primary) / 0.5);
  }
  to {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.8), 0 0 30px hsl(var(--primary) / 0.4);
  }
}

@keyframes scaleIn {
  0% {
    transform: scale(0.9);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes slideInLeft {
  0% {
    transform: translateX(-100px);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slideInRight {
  0% {
    transform: translateX(100px);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

@keyframes floating {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
  }
  33% {
    transform: translateY(-10px) rotate(2deg);
  }
  66% {
    transform: translateY(5px) rotate(-1deg);
  }
}

@keyframes gradientShift {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

@keyframes fadeInUp {
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeInDown {
  0% {
    opacity: 0;
    transform: translateY(-30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes rotateIn {
  0% {
    opacity: 0;
    transform: rotate(-200deg);
  }
  100% {
    opacity: 1;
    transform: rotate(0);
  }
}

@keyframes zoomIn {
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: scale(1);
  }
}

@keyframes elastic {
  0% {
    transform: scale(1);
  }
  30% {
    transform: scale(1.25);
  }
  40% {
    transform: scale(0.75);
  }
  50% {
    transform: scale(1.15);
  }
  65% {
    transform: scale(0.95);
  }
  75% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
}

@keyframes wobble {
  0% {
    transform: translateX(0%);
  }
  15% {
    transform: translateX(-25px) rotate(-5deg);
  }
  30% {
    transform: translateX(20px) rotate(3deg);
  }
  45% {
    transform: translateX(-15px) rotate(-3deg);
  }
  60% {
    transform: translateX(10px) rotate(2deg);
  }
  75% {
    transform: translateX(-5px) rotate(-1deg);
  }
  100% {
    transform: translateX(0%);
  }
}

@keyframes flip {
  0% {
    transform: perspective(400px) rotateY(0);
    animation-timing-function: ease-out;
  }
  40% {
    transform: perspective(400px) translateZ(150px) rotateY(170deg);
    animation-timing-function: ease-out;
  }
  50% {
    transform: perspective(400px) translateZ(150px) rotateY(190deg) scale(1);
    animation-timing-function: ease-in;
  }
  80% {
    transform: perspective(400px) rotateY(360deg) scale(0.95);
    animation-timing-function: ease-in;
  }
  100% {
    transform: perspective(400px) scale(1);
    animation-timing-function: ease-in;
  }
}

@keyframes heartBeat {
  0% {
    transform: scale(1);
  }
  14% {
    transform: scale(1.3);
  }
  28% {
    transform: scale(1);
  }
  42% {
    transform: scale(1.3);
  }
  70% {
    transform: scale(1);
  }
}

@keyframes rubberBand {
  0% {
    transform: scale(1);
  }
  30% {
    transform: scale3d(1.25, 0.75, 1);
  }
  40% {
    transform: scale3d(0.75, 1.25, 1);
  }
  50% {
    transform: scale3d(1.15, 0.85, 1);
  }
  65% {
    transform: scale3d(0.95, 1.05, 1);
  }
  75% {
    transform: scale3d(1.05, 0.95, 1);
  }
  100% {
    transform: scale(1);
  }
}

/* Custom animations for dashboard and homepage */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(16px) scale(0.96);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes slideDown {
  from {
    transform: translateY(-20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes zoomIn {
  from {
    transform: scale(0.9);
    opacity: 0;
  }
  to {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes rotateIn {
  from {
    transform: rotate(-10deg) scale(0.95);
    opacity: 0;
  }
  to {
    transform: rotate(0deg) scale(1);
    opacity: 1;
  }
}

@keyframes bounceIn {
  0% {
    transform: scale(0.3);
    opacity: 0;
  }
  50% {
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes typing {
  0%, 50% {
    border-right-color: transparent;
  }
  51%, 100% {
    border-right-color: currentColor;
  }
}

@keyframes glowPulse {
  0%, 100% {
    box-shadow: 0 0 5px rgba(59, 130, 246, 0.5);
  }
  50% {
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.8), 0 0 30px rgba(59, 130, 246, 0.4);
  }
}

@keyframes shake {
  0%, 100% {
    transform: translateX(0);
  }
  10%, 30%, 50%, 70%, 90% {
    transform: translateX(-5px);
  }
  20%, 40%, 60%, 80% {
    transform: translateX(5px);
  }
}


/* Enhanced focus and selection */
::selection {
  @apply text-primary;
  background-color: hsl(var(--primary) / 0.2);
}

/* Professional scrollbar for all elements */
* {
  scrollbar-width: thin;
  scrollbar-color: hsl(var(--muted-foreground) / 0.3) transparent;
}

*::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

*::-webkit-scrollbar-track {
  background: transparent;
}

*::-webkit-scrollbar-thumb {
  @apply bg-muted-foreground rounded-full;
  opacity: 0.2;
}

*::-webkit-scrollbar-thumb:hover {
  @apply bg-muted-foreground;
  opacity: 0.4;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .glass-card,
  .glass-sidebar {
    @apply bg-background border-border;
    backdrop-filter: none;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }
}

