import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { chatApi } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import SystemPromptDialog from "./SystemPromptDialog";
import ThemeCustomizer from "./ThemeCustomizer";
import ThemeToggle from "./ThemeToggle";
import ProfileManager from "./ProfileManager";
import FunFeatures from "./FunFeatures";
import SecurityCenter from "./SecurityCenter";
import AdvancedImageGenerator from "./AdvancedImageGenerator";
import { LanguageSettingsButton, useLanguage } from "./LanguageSelector";
import {
  Plus,
  MessageSquare,
  Trash2,
  User,
  LogOut,
  Bot,
  Sparkles,
  Calendar,
  ChevronRight,
  ChevronDown,
  Search,
  BarChart3,
  Palette,
  Gamepad2,
  Shield,
  Settings,
  Image as ImageIcon
} from "lucide-react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import type { Conversation } from "@shared/schema";

interface ChatSidebarProps {
  conversations: Conversation[];
  currentConversationId: string | null;
  onSelectConversation: (conversationId: string) => void;
  onNewChat: () => void;
}

export default function ChatSidebar({
  conversations,
  currentConversationId,
  onSelectConversation,
  onNewChat,
}: ChatSidebarProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [profileOpen, setProfileOpen] = useState(false);
  const [themeOpen, setThemeOpen] = useState(false);
  const [funOpen, setFunOpen] = useState(false);
  const [securityOpen, setSecurityOpen] = useState(false);
  const [statsOpen, setStatsOpen] = useState(false);
  const [imageGenOpen, setImageGenOpen] = useState(false);
  const { toast } = useToast();
  const { user, logoutMutation } = useAuth();
  const queryClient = useQueryClient();
  const { t } = useLanguage();

  // Statistics for conversations
  const totalMessages = conversations.length;

  // Filter conversations based on search query
  const filteredConversations = conversations.filter(conv =>
    conv.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Group conversations by date
  const groupedConversations = filteredConversations.reduce((groups, conversation) => {
    const date = new Date(conversation.createdAt);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    let groupName: string;
    if (date.toDateString() === today.toDateString()) {
      groupName = "Hoje";
    } else if (date.toDateString() === yesterday.toDateString()) {
      groupName = "Ontem";
    } else {
      groupName = format(date, "dd/MM/yyyy", { locale: ptBR });
    }

    if (!groups[groupName]) {
      groups[groupName] = [];
    }
    groups[groupName].push(conversation);
    return groups;
  }, {} as Record<string, Conversation[]>);

  // Delete conversation mutation
  const deleteConversationMutation = useMutation({
    mutationFn: async (conversationId: string) => {
      return await chatApi.deleteConversation(conversationId);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      toast({
        title: "Conversa exclu√≠da",
        description: "A conversa foi removida com sucesso.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao excluir conversa",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleDeleteConversation = (e: React.MouseEvent, conversationId: string) => {
    e.stopPropagation();
    deleteConversationMutation.mutate(conversationId);
  };

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  return (
    <div className="h-full bg-sidebar border-r border-sidebar-border flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-sidebar-border">
        <div className="flex items-center gap-3 mb-4">
          <div className="relative">
            <Avatar className="h-12 w-12 border-2 border-primary/20">
              <AvatarFallback className="bg-gradient-primary text-white font-semibold">
                <Bot className="h-6 w-6" />
              </AvatarFallback>
            </Avatar>
            <div className="absolute -bottom-1 -right-1 h-4 w-4 bg-primary rounded-full border-2 border-sidebar flex items-center justify-center">
              <Sparkles className="h-2 w-2 text-white" />
            </div>
          </div>
          <div className="flex-1 min-w-0">
            <h2 className="text-lg font-bold gradient-text truncate">Catalyst IA</h2>
            <p className="text-xs text-sidebar-foreground/70 truncate">
              {t('virtualAssistant') || 'Seu assistente virtual'}
            </p>
          </div>
        </div>

        <Button
          onClick={onNewChat}
          className="w-full justify-start gap-2 bg-primary/10 hover:bg-primary/20 text-primary font-medium border border-primary/20 hover:border-primary/40 transition-all duration-200 hover-lift animate-fade-in"
        >
          <Plus className="h-4 w-4" />
          {t('newConversation') || 'Nova Conversa'}
        </Button>
      </div>

      {/* Search */}
      <div className="p-4 border-b border-sidebar-border">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <input
            type="text"
            placeholder="Buscar conversas..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 text-sm bg-background border border-border rounded-lg
                     focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent
                     placeholder:text-muted-foreground transition-all duration-200 animate-input-border"
          />
        </div>
      </div>

      {/* Statistics */}
      <div className="p-2 space-y-2 border-b border-sidebar-border">
        <Collapsible open={statsOpen} onOpenChange={setStatsOpen}>
          <CollapsibleTrigger asChild>
            <Button
              variant="ghost"
              className="w-full justify-between hover:bg-sidebar-accent/50 h-8 animate-fade-in-slow"
            >
              <div className="flex items-center gap-2">
                <BarChart3 className="h-4 w-4 text-sidebar-foreground/60" />
                <span className="text-sm font-medium">Estat√≠sticas</span>
              </div>
              {statsOpen ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
            </Button>
          </CollapsibleTrigger>
          <CollapsibleContent className="space-y-2 mt-2 animate-slide-down">
            <div className="grid grid-cols-2 gap-2 px-2">
              <div className="card-modern p-3 animate-scale-in">
                <div className="text-xs text-muted-foreground">Conversas</div>
                <div className="text-lg font-bold text-primary">{conversations.length}</div>
              </div>
              <div className="card-modern p-3 animate-scale-in">
                <div className="text-xs text-muted-foreground">Total</div>
                <div className="text-lg font-bold text-primary">{totalMessages}</div>
              </div>
            </div>
          </CollapsibleContent>
        </Collapsible>
      </div>

      {/* Conversations List */}
      <ScrollArea className="flex-1 custom-scrollbar">
        <div className="p-2 space-y-1">
          {Object.keys(groupedConversations).length === 0 ? (
