              <Palette className="h-3 w-3 mr-2" />
              Temas
            </Button>
          </div>

          {/* Second Row */}
          <div className="grid grid-cols-2 gap-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setImageGenOpen(true)}
              className="h-8 text-xs justify-start hover:bg-sidebar-accent/50 animate-fade-in-very-fast"
