              <ImageIcon className="h-3 w-3 mr-2" />
              Gerar Imagens
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setSecurityOpen(true)}
              className="h-8 text-xs justify-start hover:bg-sidebar-accent/50 animate-fade-in-very-fast"
            >
              <Shield className="h-3 w-3 mr-2" />
