          {/* Third Row */}
          <div className="grid grid-cols-2 gap-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setFunOpen(true)}
              className="h-8 text-xs justify-start hover:bg-sidebar-accent/50 animate-fade-in-very-fast"
