                toast({
                  title: "Prompt salvo",
                  description: "O prompt do sistema foi configurado para pr√≥ximas mensagens.",
                });
              }}
              disabled={!currentConversationId}
            />
          </div>

          {/* Logout Button */}
          <Button
            variant="ghost"
            size="sm"
            onClick={handleLogout}
            disabled={logoutMutation.isPending}
            className="w-full h-8 text-xs justify-start hover:bg-destructive/20 hover:text-destructive animate-fade-in-very-fast"
          >
            <LogOut className="h-3 w-3 mr-2" />
            Sair da Conta
          </Button>
        </div>
      </div>

      {/* Modal Components */}
      {profileOpen && (
        <ProfileManager
          isOpen={profileOpen}
          onClose={() => setProfileOpen(false)}
        />
      )}

      {themeOpen && (
        <ThemeCustomizer
          isOpen={themeOpen}
          onClose={() => setThemeOpen(false)}
        />
      )}

      {funOpen && (
        <FunFeatures
          isOpen={funOpen}
          onClose={() => setFunOpen(false)}
        />
      )}

      {securityOpen && (
        <SecurityCenter
          isOpen={securityOpen}
          onClose={() => setSecurityOpen(false)}
        />
      )}

      {imageGenOpen && (
        <AdvancedImageGenerator
          isOpen={imageGenOpen}
          onClose={() => setImageGenOpen(false)}
          onGenerate={async (params) => {
            try {
              // Call the image generation API
              const response = await fetch('/api/generate-image', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                  prompt: params.prompt,
                  size: params.ratio === "1:1" ? "1024x1024" : 
                        params.ratio === "16:9" ? "1344x768" :
                        params.ratio === "9:16" ? "768x1344" : "1024x1024"
                }),
              });

              if (!response.ok) {
                throw new Error('Erro ao gerar imagem');
              }

              const data = await response.json();

              toast({
                title: "Imagem gerada com sucesso!",
                description: "A imagem foi criada usando Google Gemini.",
              });

              // You can add logic here to display or save the image
              console.log('Generated image:', data.image);

            } catch (error) {
              toast({
                title: "Erro ao gerar imagem",
                description: error instanceof Error ? error.message : "Erro desconhecido",
                variant: "destructive",
              });
            }
          }}
        />
      )}
    </div>
  );
}

