import { useState } from "react";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";
import { 
  Image as ImageIcon, 
  Download, 
  ZoomIn, 
  Calendar,
  X 
} from "lucide-react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";

interface ImageGalleryProps {
  images: Array<{
    id: string;
    url: string;
    prompt: string;
    createdAt: string;
  }>;
}

export default function ImageGallery({ images }: ImageGalleryProps) {
  const [selectedImage, setSelectedImage] = useState<string | null>(null);

  const handleDownload = (imageUrl: string, prompt: string) => {
    const link = document.createElement('a');
    link.href = imageUrl;
    link.download = `catalyst-ai-${prompt.slice(0, 30).replace(/[^a-zA-Z0-9]/g, '-')}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  if (images.length === 0) {
    return (
      <div className="text-center py-8 px-4">
        <div className="h-16 w-16 bg-muted/50 rounded-2xl flex items-center justify-center mx-auto mb-4">
          <ImageIcon className="h-8 w-8 text-muted-foreground" />
        </div>
        <h3 className="font-semibold text-sm mb-2">Nenhuma imagem gerada</h3>
        <p className="text-xs text-muted-foreground leading-relaxed">
          As imagens que voc√™ gerar aparecer√£o aqui
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center gap-2 px-3 py-2">
        <ImageIcon className="h-4 w-4 text-primary" />
        <span className="text-sm font-semibold">Galeria de Imagens ({images.length})</span>
      </div>

      <ScrollArea className="h-96 custom-scrollbar">
        <div className="grid grid-cols-2 gap-3 p-3">
          {images.map((image) => (
            <div key={image.id} className="group relative">
              <Dialog>
                <DialogTrigger asChild>
                  <div className="relative overflow-hidden rounded-lg border cursor-pointer hover-lift">
                    <img
                      src={image.url}
                      alt={image.prompt}
                      className="w-full h-24 object-cover transition-transform duration-300 group-hover:scale-105"
                    />
                    <div className="absolute inset-0 bg-black/0 group-hover:bg-black/20 transition-colors duration-300 flex items-center justify-center">
                      <ZoomIn className="h-5 w-5 text-white opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                    </div>
                  </div>
                </DialogTrigger>
                <DialogContent className="max-w-4xl">
                  <div className="space-y-4">
                    <img
                      src={image.url}
                      alt={image.prompt}
                      className="w-full rounded-lg"
                    />
                    <div className="space-y-2">
                      <p className="text-sm font-medium">Prompt:</p>
                      <p className="text-sm text-muted-foreground bg-muted p-3 rounded-lg">
                        {image.prompt}
                      </p>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2 text-xs text-muted-foreground">
                          <Calendar className="h-3 w-3" />
                          {format(new Date(image.createdAt), "dd/MM/yy '√†s' HH:mm", { locale: ptBR })}
                        </div>
                        <Button
                          size="sm"
                          onClick={() => handleDownload(image.url, image.prompt)}
                          className="h-8"
                        >
                          <Download className="h-3 w-3 mr-2" />
                          Download
                        </Button>
                      </div>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>

              <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity">
                <Button
                  size="sm"
                  variant="secondary"
                  className="h-6 w-6 p-0"
                  onClick={() => handleDownload(image.url, image.prompt)}
                >
                  <Download className="h-3 w-3" />
                </Button>
              </div>

              <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-2">
                <p className="text-white text-xs truncate">
                  {image.prompt}
                </p>
              </div>
            </div>
          ))}
        </div>
      </ScrollArea>
    </div>
  );
}

