// Context para gerenciar o idioma
interface LanguageContextType {
  currentLanguage: string;
  setLanguage: (language: string) => void;
  t: (key: string) => string;
  languages: Language[];
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

// Provider do contexto de linguagem
export const LanguageProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [currentLanguage, setCurrentLanguage] = useState<string>(() => {
    if (typeof window !== 'undefined') {
      const saved = localStorage.getItem('zero-two-language');
      return saved || 'pt-BR';
    }
    return 'pt-BR';
  });

  const setLanguage = (language: string) => {
    setCurrentLanguage(language);
    if (typeof window !== 'undefined') {
      localStorage.setItem('zero-two-language', language);
      document.documentElement.lang = language;
      // Force re-render by dispatching a custom event
      window.dispatchEvent(new CustomEvent('languageChanged', { detail: { language } }));
    }
  };

  const t = (key: string): string => {
    const translations = TRANSLATIONS[currentLanguage as keyof typeof TRANSLATIONS] || TRANSLATIONS['pt-BR'];
    return translations[key as keyof typeof translations] || key;
  };

  useEffect(() => {
    if (typeof window !== 'undefined') {
      document.documentElement.lang = currentLanguage;
    }
  }, [currentLanguage]);

  return (
    <LanguageContext.Provider value={{ currentLanguage, setLanguage, t, languages: LANGUAGES }}>
      {children}
    </LanguageContext.Provider>
  );
};

// Hook para usar o contexto de linguagem
export const useLanguage = () => {
  const context = useContext(LanguageContext);
  if (!context) {
    throw new Error('useLanguage must be used within LanguageProvider');
  }
  return context;
};

// Componente seletor de linguagem simples
export const LanguageSelector: React.FC = () => {
  const { currentLanguage, setLanguage, t, languages } = useLanguage();
  const currentLang = languages.find(lang => lang.code === currentLanguage);

  return (
    <Select value={currentLanguage} onValueChange={setLanguage}>
      <SelectTrigger className="w-full h-8 text-xs">
        <SelectValue>
          <div className="flex items-center gap-2">
            <span className="text-base">{currentLang?.flag}</span>
            <span className="truncate">{currentLang?.nativeName}</span>
          </div>
        </SelectValue>
      </SelectTrigger>
      <SelectContent>
        {languages.map((language) => (
          <SelectItem key={language.code} value={language.code}>
            <div className="flex items-center gap-3">
              <span className="text-lg">{language.flag}</span>
              <div className="flex flex-col">
                <span className="font-medium">{language.nativeName}</span>
                <span className="text-xs text-muted-foreground">{language.name}</span>
              </div>
              {currentLanguage === language.code && (
                <Check className="h-4 w-4 text-primary ml-auto" />
              )}
            </div>
          </SelectItem>
        ))}
      </SelectContent>
    </Select>
  );
};

// Componente de configura√ß√µes avan√ßadas de linguagem
export const LanguageSettings: React.FC<{ isOpen: boolean; onClose: () => void }> = ({
  isOpen,
  onClose
}) => {
  const { currentLanguage, setLanguage, t, languages } = useLanguage();
  const [tempLanguage, setTempLanguage] = useState(currentLanguage);
  const [aiLanguage, setAiLanguage] = useState(currentLanguage);

  const currentLang = languages.find(lang => lang.code === currentLanguage);
  const selectedLang = languages.find(lang => lang.code === tempLanguage);

  const handleApply = () => {
    setLanguage(tempLanguage);
    localStorage.setItem('zero-two-ai-language', aiLanguage);
    onClose();
  };

  const handleAutoDetect = () => {
    const browserLang = navigator.language;
    const matchedLang = languages.find(lang =>
      lang.code === browserLang || lang.code.split('-')[0] === browserLang.split('-')[0]
    );
    if (matchedLang) {
      setTempLanguage(matchedLang.code);
      setAiLanguage(matchedLang.code);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-2xl">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Languages className="h-5 w-5 text-primary" />
            {t('languageSettings')}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Current Language Display */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium">{t('currentLanguage')}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-4">
                <span className="text-3xl">{currentLang?.flag}</span>
                <div>
                  <p className="font-semibold">{currentLang?.nativeName}</p>
                  <p className="text-sm text-muted-foreground">{currentLang?.name}</p>
                  <Badge variant="secondary" className="text-xs mt-1">
                    {currentLang?.region}
                  </Badge>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Language Selection Grid */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">{t('chooseYourLanguage')}</h3>
              <Button variant="outline" size="sm" onClick={handleAutoDetect}>
                <Globe className="h-4 w-4 mr-2" />
                {t('autoDetect')}
              </Button>
            </div>

            <div className="grid grid-cols-2 gap-3">
              {languages.map((language) => (
                <Card
                  key={language.code}
                  className={`cursor-pointer transition-all duration-200 hover:shadow-md ${
                    tempLanguage === language.code
                      ? 'ring-2 ring-primary shadow-lg'
                      : 'hover:ring-1 hover:ring-border'
                  }`}
                  onClick={() => setTempLanguage(language.code)}
                >
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      <span className="text-2xl">{language.flag}</span>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium truncate">{language.nativeName}</p>
                        <p className="text-xs text-muted-foreground truncate">{language.name}</p>
                        <Badge variant="outline" className="text-xs mt-1">
                          {language.region}
                        </Badge>
                      </div>
                      {tempLanguage === language.code && (
                        <Check className="h-5 w-5 text-primary flex-shrink-0" />
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>

          {/* AI Language Selection */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium">{t('aiLanguage')}</CardTitle>
            </CardHeader>
            <CardContent>
              <Select value={aiLanguage} onValueChange={setAiLanguage}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {languages.map((language) => (
                    <SelectItem key={language.code} value={language.code}>
                      <div className="flex items-center gap-3">
                        <span>{language.flag}</span>
                        <span>{language.nativeName}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <p className="text-xs text-muted-foreground mt-2">
                {t('languageDescription')}
              </p>
            </CardContent>
          </Card>

          {/* Selected Language Preview */}
          {tempLanguage !== currentLanguage && selectedLang && (
            <Card className="bg-primary/5 border-primary/20">
              <CardContent className="p-4">
                <div className="flex items-center gap-3">
                  <span className="text-2xl">{selectedLang.flag}</span>
                  <div>
                    <p className="font-medium">
                      {t('changeLanguage')}: {selectedLang.nativeName}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {t('restartRequired')}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Action Buttons */}
          <div className="flex gap-3 pt-4 border-t">
            <Button variant="outline" onClick={onClose} className="flex-1">
              {t('cancel')}
            </Button>
            <Button
              onClick={handleApply}
              className="flex-1"
              disabled={tempLanguage === currentLanguage}
            >
              <Check className="h-4 w-4 mr-2" />
              {t('apply')}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

// Componente de bot√£o para abrir as configura√ß√µes
export const LanguageSettingsButton: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const { t } = useLanguage();

  return (
    <>
      <Button
        variant="ghost"
        size="sm"
        onClick={() => setIsOpen(true)}
        className="h-8 text-xs justify-start hover:bg-sidebar-accent/50"
      >
        <Globe className="h-3 w-3 mr-2" />
        {t('selectLanguage')}
      </Button>
      <LanguageSettings isOpen={isOpen} onClose={() => setIsOpen(false)} />
    </>
  );
};

export default LanguageSelector;

