  const [loading, setLoading] = useState(false);
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [showSetup2FA, setShowSetup2FA] = useState(false);
  const [biometricDevices, setBiometricDevices] = useState<any[]>([]);
  const [loginHistory, setLoginHistory] = useState<any[]>([
    { id: 1, timestamp: "2024-01-20 14:30", device: "Chrome - Windows", location: "S√£o Paulo, BR", status: "success" },
    { id: 2, timestamp: "2024-01-20 09:15", device: "iPhone App", location: "S√£o Paulo, BR", status: "success" },
    { id: 3, timestamp: "2024-01-19 22:45", device: "Chrome - Windows", location: "S√£o Paulo, BR", status: "success" },
    { id: 4, timestamp: "2024-01-19 18:30", device: "Unknown Device", location: "Rio de Janeiro, BR", status: "blocked" },
  ]);

  const [sessions, setSessions] = useState([
    { id: 1, device: "Chrome - Windows", location: "S√£o Paulo, BR", lastActive: "Agora", current: true },
    { id: 2, device: "Safari - iPhone", location: "S√£o Paulo, BR", lastActive: "2h atr√°s", current: false },
