      // Simula√ß√£o de configura√ß√£o 2FA
      setTimeout(() => {
        setQrCodeUrl('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg==');
        setShowSetup2FA(true);
        setLoading(false);
