  };

  const enable2FA = async () => {
    if (!verificationCode || verificationCode.length !== 6) {
      toast({
        title: "Erro",
        description: "Digite um c√≥digo de 6 d√≠gitos v√°lido",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);
    try {
      // Simula√ß√£o de ativa√ß√£o 2FA
      setTimeout(() => {
        setSecurity(prev => ({ ...prev, twoFactorEnabled: true }));
        setShowSetup2FA(false);
        setVerificationCode('');
