        toast({
          title: "Sucesso!",
          description: "2FA ativado com sucesso. Sua conta est√° mais segura!",
        });
      }, 1000);
    } catch (error: any) {
      setLoading(false);
      toast({
        title: "Erro",
        description: "C√≥digo inv√°lido. Tente novamente.",
        variant: "destructive",
      });
    }
  };

  const disable2FA = async () => {
    setLoading(true);
    try {
      setTimeout(() => {
        setSecurity(prev => ({ ...prev, twoFactorEnabled: false }));
        setLoading(false);
        toast({
          title: "2FA Desativado",
          description: "Autentica√ß√£o de dois fatores foi desativada",
        });
