        });
      }, 1500);
    } catch (error: any) {
      setLoading(false);
      toast({
        title: "Erro",
        description: "Falha ao registrar dispositivo biom√©trico. Verifique se seu dispositivo suporta WebAuthn.",
        variant: "destructive",
      });
    }
  };

  const removeDevice = async (deviceId: string) => {
    try {
      setBiometricDevices(prev => prev.filter(device => device.id !== deviceId));
      toast({
        title: "Sucesso!",
        description: "Dispositivo removido com sucesso",
      });
    } catch (error: any) {
      toast({
        title: "Erro",
        description: "Erro ao remover dispositivo",
        variant: "destructive",
      });
    }
  };

  const revokeSession = (sessionId: number) => {
    setSessions(prev => prev.filter(s => s.id !== sessionId));
    toast({
      title: "Sess√£o revogada",
      description: "A sess√£o foi finalizada com sucesso.",
    });
  };

  const exportData = () => {
    toast({
      title: "Exporta√ß√£o iniciada",
      description: "Seus dados ser√£o exportados e enviados por email em at√© 24 horas.",
    });
  };

  const deleteAccount = () => {
    if (window.confirm("ATEN√á√ÉO: Esta a√ß√£o √© irrevers√≠vel. Tem certeza que deseja excluir sua conta?")) {
      toast({
        title: "Conta agendada para exclus√£o",
        description: "Sua conta ser√° exclu√≠da em 30 dias. Voc√™ pode cancelar at√© l√°.",
        variant: "destructive",
      });
    }
  };

  if (!isOpen) return null;

  return (
